mapper.select:
select

from f_user_account
where uname = #{uname}
or account = #{uname}
or mobile = #{uname}
or email = #{uname}

		select distinct
		r.role_type
		from
		f_user_account u
		left join f_user_role ur on u.id = ur.user_id
		left join f_role r on ur.role_id = r.id
		where u.uname = #{uname}
		or account = #{uname}
        or mobile = #{uname}
        or email = #{uname}

		select distinct
		p.permission_name
		from f_user_account u
		left join f_user_role ur on u.id = ur.user_id
		left join f_role r on ur.role_id = r.id
		left join f_role_permission rp on rp.role_id = r.id
		left join f_permission p on rp.permission_id = p.id
		where u.uname = #{uname}
		or account = #{uname}
        or mobile = #{uname}
        or email = #{uname}
